body {background-color:#000000; background-image:url(/backgroundimage/pinkstars.gif); color:#000000; background-position: center center; background-repeat: repeat; background-attachment: fixed; font-family : Comic Sans MS; font-size:medium; margin: 0px; padding: 1px; color:#FFFFFF;} p {padding:0px;} input, select {color:#F1FD1A; border: 1px ridge #FFFFFF; font-family: Comic Sans MS, Tahoma, Verdana; font-weight: bold; background-color:#000000;} * {text-decoration:none;} img {border: 0px none;} a, a:active, a:visited, a:hover {color:#F1FD1A;} head {font-family : Comic Sans MS; font-size:medium;} div {margin: 2px 0px 2px 0px; padding: 2px 0px 2px 0px; color:#000000; background-color:#003399; border: 2px solid #FFFFFF; } .center {text-align: center;} .left {text-align: center;} .link {color:#FFFFFF;}